import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();

// CORS configuration for split deployment
app.use((req, res, next) => {
  const origin = req.headers.origin;
  const allowedOrigins = [
    'http://localhost:5173',
    'http://localhost:3000', 
    'https://farcastai.vercel.app', // Update with your Vercel URL
    ...(process.env.FRONTEND_URL ? [process.env.FRONTEND_URL] : [])
  ];

  if (origin && allowedOrigins.includes(origin)) {
    res.setHeader('Access-Control-Allow-Origin', origin);
    res.setHeader('Vary', 'Origin');
  }

  res.setHeader('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE, OPTIONS');
  res.setHeader('Access-Control-Allow-Headers', 'Content-Type, Authorization');
  res.setHeader('Access-Control-Allow-Credentials', 'true');

  if (req.method === 'OPTIONS') {
    res.sendStatus(200);
    return;
  }

  next();
});

app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  // Serve static files FIRST - before any other routes or middleware
  app.get('/icon.png', (req, res) => {
    res.sendFile('icon.png', { root: './client/public' });
  });

  app.get('/logo.png', (req, res) => {
    res.sendFile('logo.png', { root: './client/public' });
  });

  app.get('/splash.png', (req, res) => {
    res.sendFile('splash.png', { root: './client/public' });
  });

  app.get('/og.png', (req, res) => {
    res.sendFile('og.png', { root: './client/public' });
  });

  app.get('/image.png', (req, res) => {
    res.sendFile('image.png', { root: './client/public' });
  });

  // Redirect to Farcaster hosted manifest
  app.get('/.well-known/farcaster.json', (req, res) => {
    res.redirect(307, 'https://api.farcaster.xyz/miniapps/hosted-manifest/019965e1-c784-0cb0-1fb2-104aae4c9b2d');
  });

  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { aiService } from "./services/openai";
import { pexelsService } from "./services/pexels";
import { farcasterService } from "./services/farcaster";
import { insertUserSchema, insertContentDraftSchema } from "@shared/schema";
import { z } from "zod";

const generateContentSchema = z.object({
  topic: z.string().min(1, "Topic is required"),
  contentType: z.string().min(1, "Content type is required"),
  tone: z.string().min(1, "Tone is required"),
});

const publishCastSchema = z.object({
  draftId: z.string().min(1, "Draft ID is required"),
  imageUrl: z.string().optional(),
});

export async function registerRoutes(app: Express): Promise<Server> {
  // User routes
  app.post("/api/users", async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByWalletAddress(userData.walletAddress);
      if (existingUser) {
        return res.json(existingUser);
      }

      const user = await storage.createUser(userData);
      res.json(user);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.get("/api/users/:walletAddress", async (req, res) => {
    try {
      const user = await storage.getUserByWalletAddress(req.params.walletAddress);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.patch("/api/users/:id", async (req, res) => {
    try {
      const updates = req.body;
      const user = await storage.updateUser(req.params.id, updates);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Content generation routes
  app.post("/api/content/generate", async (req, res) => {
    try {
      const { topic, contentType, tone } = generateContentSchema.parse(req.body);

      const generatedContent = await aiService.generateContent(topic, contentType, tone);
      res.json({ content: generatedContent });
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Content draft routes
  app.post("/api/drafts", async (req, res) => {
    try {
      const draftData = insertContentDraftSchema.parse(req.body);
      const draft = await storage.createContentDraft(draftData);
      res.json(draft);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.get("/api/drafts/user/:userId", async (req, res) => {
    try {
      const drafts = await storage.getContentDraftsByUserId(req.params.userId);
      res.json(drafts);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.patch("/api/drafts/:id", async (req, res) => {
    try {
      const updates = req.body;
      const draft = await storage.updateContentDraft(req.params.id, updates);
      if (!draft) {
        return res.status(404).json({ message: "Draft not found" });
      }
      res.json(draft);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.delete("/api/drafts/:id", async (req, res) => {
    try {
      const success = await storage.deleteContentDraft(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Draft not found" });
      }
      res.json({ success: true });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Image search routes
  app.get("/api/images/search", async (req, res) => {
    try {
      const query = req.query.q as string;
      const perPage = parseInt(req.query.per_page as string) || 12;

      if (!query) {
        return res.status(400).json({ message: "Search query is required" });
      }

      const photos = await pexelsService.searchPhotos(query, perPage);
      res.json(photos);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.get("/api/images/featured", async (req, res) => {
    try {
      const perPage = parseInt(req.query.per_page as string) || 12;
      const photos = await pexelsService.getFeaturedPhotos(perPage);
      res.json(photos);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Farcaster casting routes
  app.post("/api/farcaster/cast", async (req, res) => {
    try {
      const { draftId, imageUrl } = publishCastSchema.parse(req.body);

      const draft = await storage.getContentDraft(draftId);
      if (!draft) {
        return res.status(404).json({ message: "Draft not found" });
      }

      const user = await storage.getUser(draft.userId);
      if (!user) {
        return res.status(400).json({ message: "User not found" });
      }

      // If user doesn't have Farcaster FID, use demo mode
      let farcasterFid = user.farcasterFid;
      if (!farcasterFid) {
        // Demo mode: Use a placeholder FID for testing
        farcasterFid = "123456";
        console.log("Demo mode: Using placeholder FID for casting");
      }

      // Prepare cast for manual submission instead of auto-posting
      const castPreparation = await farcasterService.prepareCast(
        farcasterFid,
        draft.generatedContent || "",
        imageUrl
      );

      // Mark as prepared but not yet published
      await storage.updateContentDraft(draftId, {
        isPublished: false, // Will be true when user manually posts
        farcasterCastHash: null, // Will be set when user provides it
      });

      res.json({
        ...castPreparation,
        message: "Cast prepared for manual posting to Farcaster"
      });
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.get("/api/farcaster/profile/:fid", async (req, res) => {
    try {
      const profile = await farcasterService.getUserProfile(req.params.fid);
      res.json(profile);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.get("/api/farcaster/user-by-wallet/:walletAddress", async (req, res) => {
    try {
      const farcasterUser = await farcasterService.getUserByWalletAddress(req.params.walletAddress);
      if (!farcasterUser) {
        return res.status(404).json({ message: "User not found on Farcaster" });
      }
      res.json(farcasterUser);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Webhook for Farcaster events
  app.post("/api/webhook", async (req, res) => {
    try {
      // Handle Farcaster server events (notifications, etc.)
      console.log("Farcaster webhook received:", req.body);
      res.status(200).json({ success: true });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type ContentDraft, type InsertContentDraft } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByWalletAddress(walletAddress: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<User>): Promise<User | undefined>;

  // Content draft methods
  getContentDraft(id: string): Promise<ContentDraft | undefined>;
  getContentDraftsByUserId(userId: string): Promise<ContentDraft[]>;
  createContentDraft(draft: InsertContentDraft): Promise<ContentDraft>;
  updateContentDraft(id: string, updates: Partial<ContentDraft>): Promise<ContentDraft | undefined>;
  deleteContentDraft(id: string): Promise<boolean>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private contentDrafts: Map<string, ContentDraft>;

  constructor() {
    this.users = new Map();
    this.contentDrafts = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByWalletAddress(walletAddress: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.walletAddress === walletAddress,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { 
      ...insertUser,
      id,
      farcasterFid: insertUser.farcasterFid || null,
      farcasterUsername: insertUser.farcasterUsername || null,
      farcasterDisplayName: insertUser.farcasterDisplayName || null,
      farcasterAvatar: insertUser.farcasterAvatar || null,
      createdAt: new Date()
    };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const updatedUser = { ...user, ...updates };
    this.users.set(id, updatedUser);
    return updatedUser;
  }

  async getContentDraft(id: string): Promise<ContentDraft | undefined> {
    return this.contentDrafts.get(id);
  }

  async getContentDraftsByUserId(userId: string): Promise<ContentDraft[]> {
    return Array.from(this.contentDrafts.values()).filter(
      (draft) => draft.userId === userId,
    );
  }

  async createContentDraft(insertDraft: InsertContentDraft): Promise<ContentDraft> {
    const id = randomUUID();
    const draft: ContentDraft = {
      id,
      userId: insertDraft.userId,
      topic: insertDraft.topic,
      contentType: insertDraft.contentType,
      tone: insertDraft.tone,
      generatedContent: insertDraft.generatedContent || null,
      selectedImage: insertDraft.selectedImage || null,
      isPublished: insertDraft.isPublished || false,
      farcasterCastHash: insertDraft.farcasterCastHash || null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.contentDrafts.set(id, draft);
    return draft;
  }

  async updateContentDraft(id: string, updates: Partial<ContentDraft>): Promise<ContentDraft | undefined> {
    const draft = this.contentDrafts.get(id);
    if (!draft) return undefined;

    const updatedDraft = { 
      ...draft, 
      ...updates, 
      updatedAt: new Date() 
    };
    this.contentDrafts.set(id, updatedDraft);
    return updatedDraft;
  }

  async deleteContentDraft(id: string): Promise<boolean> {
    return this.contentDrafts.delete(id);
  }
}

export const storage = new MemStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, json, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  walletAddress: text("wallet_address").notNull().unique(),
  farcasterFid: text("farcaster_fid"),
  farcasterUsername: text("farcaster_username"),
  farcasterDisplayName: text("farcaster_display_name"),
  farcasterAvatar: text("farcaster_avatar"),
  createdAt: timestamp("created_at").defaultNow(),
});

export const contentDrafts = pgTable("content_drafts", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id),
  topic: text("topic").notNull(),
  contentType: text("content_type").notNull(),
  tone: text("tone").notNull(),
  generatedContent: text("generated_content"),
  selectedImage: json("selected_image").$type<{
    url: string;
    alt: string;
    photographer: string;
    source: string;
  }>(),
  isPublished: boolean("is_published").default(false).notNull(),
  farcasterCastHash: text("farcaster_cast_hash"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
});

export const insertContentDraftSchema = createInsertSchema(contentDrafts).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertContentDraft = z.infer<typeof insertContentDraftSchema>;
export type ContentDraft = typeof contentDrafts.$inferSelect;

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { ThemeProvider } from "@/components/theme-provider";
import { WalletProvider } from "@/hooks/use-wallet";
import { LanguageProvider } from "@/components/language-provider";
import Home from "@/pages/home";
import NotFound from "@/pages/not-found";
import { useEffect } from "react";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Home} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  // Initialize Farcaster SDK
  useEffect(() => {
    const initializeFarcasterSDK = async () => {
      try {
        if (typeof window !== 'undefined') {
          const { sdk } = await import('@farcaster/miniapp-sdk');
          // Signal that the app is ready - this will hide the splash screen
          await sdk.actions.ready();
          console.log('Farcaster SDK initialized successfully');
        }
      } catch (error) {
        console.warn('Failed to initialize Farcaster SDK:', error);
      }
    };

    initializeFarcasterSDK();
  }, []);

  return (
    <QueryClientProvider client={queryClient}>
      <LanguageProvider>
        <ThemeProvider defaultTheme="dark" storageKey="farcastai-theme">
          <WalletProvider>
            <TooltipProvider>
              <Toaster />
              <Router />
            </TooltipProvider>
          </WalletProvider>
        </ThemeProvider>
      </LanguageProvider>
    </QueryClientProvider>
  );
}

export default App;

