server/services/farcaster.ts.content:
interface FarcasterCastRequest {
  text: string;
  embeds?: Array<{
    url?: string;
    castId?: {
      fid: number;
      hash: string;
    };
  }>;
  embedsDeprecated?: string[];
  mentions?: number[];
  mentionsPositions?: number[];
}

interface FarcasterCastResponse {
  hash: string;
  success: boolean;
}

export class FarcasterService {
  private hubUrl: string;
  private warpcastApiUrl: string;

  constructor() {
    // Using Farcaster Hub API for direct casting
    this.hubUrl = process.env.FARCASTER_HUB_URL || 'https://hub.farcaster.xyz';
    this.warpcastApiUrl = 'https://api.warpcast.com';
  }

  async prepareCast(
    fid: string, 
    text: string, 
    imageUrl?: string
  ): Promise<{ castContent: string; farcasterUrl: string; ready: boolean }> {
    try {
      // Prepare cast for manual submission
      console.log('Preparing Farcaster cast for FID:', fid);
      console.log('Cast content:', text);
      if (imageUrl) {
        console.log('With image:', imageUrl);
      }

      // Simulate preparation delay
      await new Promise(resolve => setTimeout(resolve, 500));

      // Create direct Farcaster cast URL for manual posting
      const encodedText = encodeURIComponent(text);
      let farcasterUrl = `https://warpcast.com/~/compose?text=${encodedText}`;

      if (imageUrl) {
        const encodedImage = encodeURIComponent(imageUrl);
        farcasterUrl += `&embeds[]=${encodedImage}`;
      }

      return {
        castContent: text,
        farcasterUrl,
        ready: true,
      };
    } catch (error) {
      console.error('Error preparing cast:', error);
      throw new Error('Failed to prepare cast for Farcaster');
    }
  }

  async getUserProfile(fid: string): Promise<any> {
    try {
      const response = await fetch(`${this.hubUrl}/v1/userDataByFid?fid=${fid}`);

      if (!response.ok) {
        throw new Error(`Failed to fetch user profile: ${response.statusText}`);
      }

      return await response.json();
    } catch (error) {
      console.error('Error fetching Farcaster user profile:', error);
      throw new Error('Failed to fetch user profile from Farcaster');
    }
  }

  async getUserByWalletAddress(walletAddress: string): Promise<any> {
    try {
      // Using Warpcast API to get user by connected wallet address
      const response = await fetch(`${this.warpcastApiUrl}/v2/user-by-verification?address=${walletAddress}`);

      if (!response.ok) {
        if (response.status === 404) {
          return null; // User not found on Farcaster
        }
        throw new Error(`Failed to fetch user by wallet: ${response.statusText}`);
      }

      const data = await response.json();
      return {
        fid: data.result?.user?.fid,
        username: data.result?.user?.username,
        displayName: data.result?.user?.displayName,
        pfpUrl: data.result?.user?.pfp?.url,
      };
    } catch (error) {
      console.error('Error fetching Farcaster user by wallet:', error);
      return null; // Return null instead of throwing to allow user creation without Farcaster
    }
  }
}

export const farcasterService = new FarcasterService();

server/services/openai.ts.content:
import { GoogleGenAI } from "@google/genai";

// Using Gemini 2.5 Flash as the default model for content generation
// DON'T DELETE THIS COMMENT
// Follow these instructions when using this blueprint:
// - Note that the newest Gemini model series is "gemini-2.5-flash" or gemini-2.5-pro"
//   - do not change this unless explicitly requested by the user

const ai = new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY || "" });

export class AIService {
  async generateContent(topic: string, contentType: string, tone: string): Promise<string> {
    try {
      const prompt = this.buildContentPrompt(topic, contentType, tone);

      const systemInstruction = "You are an expert content creator for Farcaster, a decentralized social network. Create engaging, authentic content that follows Farcaster's culture and best practices. IMPORTANT: Keep content SHORT and CONCISE - maximum 1 paragraph only. Always include relevant emojis and structure content for social media engagement.";

      const response = await ai.models.generateContent({
        model: "gemini-2.5-flash",
        config: {
          systemInstruction,
        },
        contents: prompt,
      });

      const content = response.text;
      if (!content) {
        throw new Error("No content generated");
      }

      return content;
    } catch (error: any) {
      console.error("Error generating content with OpenAI:", error);
      if (error.code === 'invalid_api_key') {
        throw new Error("Invalid Gemini API key. Please check your configuration.");
      }
      throw new Error(`Failed to generate content: ${error.message}`);
    }
  }

  private buildContentPrompt(topic: string, contentType: string, tone: string): string {
    const basePrompt = `Create a ${contentType.toLowerCase()} about "${topic}" in a ${tone.toLowerCase()} tone for Farcaster.`;

    let specificInstructions = "";

    switch (contentType.toLowerCase()) {
      case "educational":
        specificInstructions = `
        - Share one key insight or takeaway in a single paragraph
        - Use clear, digestible information 
        - Add 1-2 relevant emojis to enhance readability
        - End with a brief thought-provoking question
        - Keep it concise and focused on one main point`;
        break;

      case "news":
        specificInstructions = `
        - Start with the key news point in one sentence
        - Briefly explain why it matters
        - Keep it timely and relevant
        - Use 1-2 emojis to highlight importance`;
        break;

      case "personal":
        specificInstructions = `
        - Share a brief personal perspective
        - Be authentic and relatable
        - Include one key lesson learned
        - Connect with the audience on a personal level`;
        break;

      case "analysis":
        specificInstructions = `
        - Focus on one key industry insight
        - Provide the most important data point
        - Mention the main implication
        - End with a brief future outlook`;
        break;

      case "creative":
        specificInstructions = `
        - Tell a brief, engaging story
        - Use one creative element
        - Create emotional connection
        - Have a satisfying conclusion`;
        break;

      default:
        specificInstructions = `
        - Create engaging, shareable content in one paragraph
        - Use 1-2 appropriate emojis
        - Keep it concise but informative
        - Include a call to action or question`;
    }

    const toneGuidance = this.getToneGuidance(tone.toLowerCase());

    return `${basePrompt}

Requirements:
${specificInstructions}

Tone guidance:
${toneGuidance}

Format:
- Write for Farcaster's audience (crypto-native, tech-savvy, engaged)
- MAXIMUM 1 PARAGRAPH - keep it short and to the point
- Aim for 100-200 characters total (very concise)
- Include 1-2 relevant emojis maximum
- Make it engaging and likely to generate replies/recasts
- NO long explanations or multiple points

Generate SHORT, FOCUSED content ready to be posted to Farcaster.`;
  }

  private getToneGuidance(tone: string): string {
    switch (tone) {
      case "professional":
        return `
        - Use authoritative but approachable language
        - Include industry terminology appropriately
        - Maintain credibility while being accessible
        - Focus on value and insights`;

      case "casual":
        return `
        - Use conversational, friendly language
        - Include casual expressions and slang when appropriate
        - Be relatable and down-to-earth
        - Feel like talking to a knowledgeable friend`;

      case "humorous":
        return `
        - Include appropriate humor and wit
        - Use clever observations or funny analogies
        - Keep it light but still informative
        - Make people smile while learning`;

      default:
        return `
        - Use clear, engaging language
        - Be authentic and genuine
        - Focus on providing value to readers`;
    }
  }

  async analyzeContent(content: string): Promise<{
    sentiment: string;
    readability: string;
    engagement_potential: string;
    suggestions: string[];
  }> {
    try {
      const systemInstruction = `You are a social media content analyst. Analyze content for Farcaster and provide insights in JSON format with these fields:
            - sentiment: overall sentiment (positive/negative/neutral)
            - readability: how easy it is to read (high/medium/low)
            - engagement_potential: likelihood of engagement (high/medium/low)
            - suggestions: array of 2-3 specific improvement suggestions`;

      const response = await ai.models.generateContent({
        model: "gemini-2.5-pro",
        config: {
          systemInstruction,
          responseMimeType: "application/json",
          responseSchema: {
            type: "object",
            properties: {
              sentiment: { type: "string" },
              readability: { type: "string" },
              engagement_potential: { type: "string" },
              suggestions: { type: "array", items: { type: "string" } }
            },
            required: ["sentiment", "readability", "engagement_potential", "suggestions"]
          }
        },
        contents: `Analyze this Farcaster content: "${content}"`
      });

      const analysis = JSON.parse(response.text || "{}");
      return {
        sentiment: analysis.sentiment || "neutral",
        readability: analysis.readability || "medium",
        engagement_potential: analysis.engagement_potential || "medium",
        suggestions: analysis.suggestions || []
      };
    } catch (error: any) {
      console.error("Error analyzing content:", error);
      throw new Error("Failed to analyze content");
    }
  }

  async generateImagePrompt(content: string): Promise<string> {
    try {
      const systemInstruction = "You are an expert at creating search queries for stock photos. Based on content, generate a concise search term that would find relevant, professional images.";

      const response = await ai.models.generateContent({
        model: "gemini-2.5-flash",
        config: {
          systemInstruction,
        },
        contents: `Generate a search term for finding relevant images for this content: "${content.slice(0, 500)}"`
      });

      return response.text?.trim() || "technology";
    } catch (error: any) {
      console.error("Error generating image prompt:", error);
      return "technology"; // Fallback
    }
  }
}

export const aiService = new AIService();

server/services/pexels.ts.content:
interface PexelsPhoto {
  id: number;
  width: number;
  height: number;
  url: string;
  photographer: string;
  photographer_url: string;
  photographer_id: number;
  avg_color: string;
  src: {
    original: string;
    large2x: string;
    large: string;
    medium: string;
    small: string;
    portrait: string;
    landscape: string;
    tiny: string;
  };
  alt: string;
}

interface PexelsSearchResponse {
  page: number;
  per_page: number;
  photos: PexelsPhoto[];
  total_results: number;
  next_page?: string;
}

export class PexelsService {
  private apiKey: string;
  private baseUrl = 'https://api.pexels.com/v1';

  constructor() {
    this.apiKey = process.env.PEXELS_API_KEY || process.env.PEXELS_KEY || '';
    if (!this.apiKey) {
      throw new Error('PEXELS_API_KEY environment variable is required');
    }
  }

  async searchPhotos(query: string, perPage: number = 6): Promise<PexelsPhoto[]> {
    try {
      const response = await fetch(
        `${this.baseUrl}/search?query=${encodeURIComponent(query)}&per_page=${perPage}&orientation=landscape`,
        {
          headers: {
            'Authorization': this.apiKey,
            'Content-Type': 'application/json',
          },
        }
      );

      if (!response.ok) {
        throw new Error(`Pexels API error: ${response.status} ${response.statusText}`);
      }

      const data: PexelsSearchResponse = await response.json();
      return data.photos;
    } catch (error) {
      console.error('Error searching Pexels photos:', error);
      throw new Error('Failed to search photos from Pexels');
    }
  }

  async getFeaturedPhotos(perPage: number = 6): Promise<PexelsPhoto[]> {
    try {
      // Use crypto-related search terms with rotation based on date
      const cryptoTerms = [
        'cryptocurrency bitcoin',
        'blockchain technology',
        'digital currency',
        'ethereum trading',
        'crypto investment',
        'bitcoin mining',
        'blockchain network',
        'cryptocurrency exchange'
      ];

      // Rotate search term daily
      const dayOfYear = Math.floor(Date.now() / (1000 * 60 * 60 * 24));
      const selectedTerm = cryptoTerms[dayOfYear % cryptoTerms.length];

      const response = await fetch(
        `${this.baseUrl}/search?query=${encodeURIComponent(selectedTerm)}&per_page=${perPage}&orientation=landscape`,
        {
          headers: {
            'Authorization': this.apiKey,
            'Content-Type': 'application/json',
          },
        }
      );

      if (!response.ok) {
        throw new Error(`Pexels API error: ${response.status} ${response.statusText}`);
      }

      const data: PexelsSearchResponse = await response.json();
      return data.photos;
    } catch (error) {
      console.error('Error fetching featured Pexels photos:', error);
      throw new Error('Failed to fetch featured photos from Pexels');
    }
  }
}

export const pexelsService = new PexelsService();

client/src/components/content-generator..content:
import { useState, useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { CONTENT_TYPES, TONE_OPTIONS } from "@/lib/constants";
import { useWallet } from "@/hooks/use-wallet";
import { useLanguage } from "@/components/language-provider";
import { ContentSuggestions } from "@/components/content-suggestions";
import { Zap, Loader2, Edit3, AlertCircle, CheckCircle } from "lucide-react";

interface ContentGeneratorProps {
  onContentGenerated: (content: string, source: 'ai' | 'manual') => void;
}

export function ContentGenerator({ onContentGenerated }: ContentGeneratorProps) {
  const [topic, setTopic] = useState("");
  const [contentType, setContentType] = useState("");
  const [tone, setTone] = useState("professional");
  const [manualContent, setManualContent] = useState("");
  const [activeTab, setActiveTab] = useState("ai");
  const [lastError, setLastError] = useState<string | null>(null);
  const { user } = useWallet();
  const { toast } = useToast();
  const { t } = useLanguage();
  const queryClient = useQueryClient();

  // Clear form data when language changes
  useEffect(() => {
    const handleLanguageChange = () => {
      setTopic("");
      setContentType("");
      setTone("professional");
      setManualContent("");
      onContentGenerated("", 'manual'); // Clear generated content too
    };

    window.addEventListener('languageChanged', handleLanguageChange);
    return () => window.removeEventListener('languageChanged', handleLanguageChange);
  }, [onContentGenerated]);

  const generateContentMutation = useMutation({
    mutationFn: async (data: { topic: string; contentType: string; tone: string }) => {
      const response = await apiRequest("POST", "/api/content/generate", data);
      return await response.json();
    },
    onSuccess: (data) => {
      onContentGenerated(data.content, 'ai');
      setLastError(null);
      toast({
        title: t('toast.contentGenerated'),
        description: t('toast.contentReady'),
      });
    },
    onError: (error: any) => {
      let errorMessage = error.message || t('toast.failedToGenerate');
      let errorDescription = t('toast.tryAgainLater');

      // Handle specific AI API errors
      if (error.message?.includes("quota") || error.message?.includes("billing")) {
        errorMessage = t('toast.quotaExceeded');
        errorDescription = t('toast.quotaExceededDesc');
      } else if (error.message?.includes("api_key") || error.message?.includes("unauthorized")) {
        errorMessage = t('toast.configIssue');
        errorDescription = t('toast.configIssueDesc');
      } else if (error.message?.includes("rate_limit")) {
        errorMessage = t('toast.tooManyRequests');
        errorDescription = t('toast.tooManyRequestsDesc');
      }

      setLastError(errorMessage);
      toast({
        title: errorMessage,
        description: errorDescription,
        variant: "destructive",
      });
    },
  });

  const saveDraftMutation = useMutation({
    mutationFn: async (draftData: any) => {
      const response = await apiRequest("POST", "/api/drafts", draftData);
      return await response.json();
    },
    onSuccess: () => {
      toast({
        title: t('toast.draftSaved'),
        description: t('toast.draftSavedDesc'),
      });
      queryClient.invalidateQueries({ queryKey: ["/api/drafts/user", user?.id] });
    },
    onError: (error: any) => {
      toast({
        title: t('toast.saveFailed'),
        description: error.message || t('toast.saveFailedDesc'),
        variant: "destructive",
      });
    },
  });

  const handleGenerate = () => {
    if (!topic.trim()) {
      toast({
        title: t('toast.topicRequired'),
        description: t('toast.topicRequiredDesc'),
        variant: "destructive",
      });
      return;
    }

    if (!contentType) {
      toast({
        title: t('toast.contentTypeRequired'),
        description: t('toast.contentTypeRequiredDesc'),
        variant: "destructive",
      });
      return;
    }

    generateContentMutation.mutate({ topic: topic.trim(), contentType, tone });
  };

  const handleManualSubmit = () => {
    if (!manualContent.trim()) {
      toast({
        title: "Content required",
        description: "Please enter your content",
        variant: "destructive",
      });
      return;
    }

    if (manualContent.trim().length < 10) {
      toast({
        title: "Content too short",
        description: "Please enter at least 10 characters",
        variant: "destructive",
      });
      return;
    }

    onContentGenerated(manualContent.trim(), 'manual');
    toast({
      title: t('toast.manualContentReady'),
      description: t('toast.manualContentReadyDesc'),
    });
  };

  const handleSaveDraft = () => {
    if (!user) {
      toast({
        title: "Wallet not connected",
        description: "Please connect your wallet to save drafts",
        variant: "destructive",
      });
      return;
    }

    let draftContent = null;
    let draftTopic = "";

    if (activeTab === "ai") {
      if (!topic.trim() || !contentType) {
        toast({
          title: "Missing information",
          description: "Please fill in topic and content type before saving",
          variant: "destructive",
        });
        return;
      }
      draftTopic = topic.trim();
    } else {
      if (!manualContent.trim()) {
        toast({
          title: "Missing content",
          description: "Please enter your content before saving",
          variant: "destructive",
        });
        return;
      }
      draftTopic = manualContent.trim().slice(0, 50) + "...";
      draftContent = manualContent.trim();
    }

    const draftData = {
      userId: user.id,
      topic: draftTopic,
      contentType: activeTab === "ai" ? contentType : "manual",
      tone: activeTab === "ai" ? tone : "custom",
      generatedContent: draftContent,
      selectedImage: null,
      isPublished: false,
    };

    saveDraftMutation.mutate(draftData);
  };

  return (
    <Card className="content-card border border-border h-full flex flex-col">
      <CardContent className="p-6 flex-1">
        <h2 className="text-xl font-semibold text-foreground mb-6 flex items-center">
          <Zap className="w-6 h-6 mr-2 text-accent" />
          {t('content.generator')}
        </h2>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <TabsList className="grid w-full grid-cols-2 mb-6">
            <TabsTrigger value="ai" className="flex items-center gap-2" data-testid="tab-ai-mode">
              <Zap className="w-4 h-4" />
              {t('content.aiGeneration')}
            </TabsTrigger>
            <TabsTrigger value="manual" className="flex items-center gap-2" data-testid="tab-manual-mode">
              <Edit3 className="w-4 h-4" />
              {t('content.manualInput')}
            </TabsTrigger>
          </TabsList>

          <TabsContent value="ai" className="space-y-4">
            {/* Error display for AI mode */}
            {lastError && (
              <div className="flex items-center gap-2 p-3 bg-destructive/10 border border-destructive/20 rounded-lg">
                <AlertCircle className="w-4 h-4 text-destructive" />
                <div className="flex-1">
                  <p className="text-sm font-medium text-destructive">{lastError}</p>
                  <p className="text-xs text-muted-foreground">Try using the Manual Input mode as an alternative</p>
                </div>
              </div>
            )}

            <div>
              <Label htmlFor="topic" className="text-sm font-medium text-foreground mb-2 block">
                {t('content.topic')}
              </Label>
              <Input
                id="topic"
                type="text"
                placeholder={t('content.topic.placeholder')}
                value={topic}
                onChange={(e) => setTopic(e.target.value)}
                className="w-full"
                data-testid="input-topic"
              />
            </div>

            {/* Quick Suggestions */}
            <ContentSuggestions onSuggestionClick={(suggestedTopic) => setTopic(suggestedTopic)} />

            <div>
              <Label htmlFor="content-type" className="text-sm font-medium text-foreground mb-2 block">
                {t('content.type')}
              </Label>
              <Select value={contentType} onValueChange={setContentType}>
                <SelectTrigger className="w-full" data-testid="select-content-type">
                  <SelectValue placeholder="Select content type" />
                </SelectTrigger>
                <SelectContent>
                  {CONTENT_TYPES.map((type) => (
                    <SelectItem key={type.value} value={type.value}>
                      {type.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label className="text-sm font-medium text-foreground mb-2 block">{t('content.tone')}</Label>
              <div className="grid grid-cols-3 gap-2">
                {TONE_OPTIONS.map((toneOption) => (
                  <Button
                    key={toneOption.value}
                    variant={tone === toneOption.value ? "default" : "secondary"}
                    size="sm"
                    onClick={() => setTone(toneOption.value)}
                    className={tone === toneOption.value ? "bg-primary text-primary-foreground" : ""}
                    data-testid={`button-tone-${toneOption.value}`}
                  >
                    {toneOption.label}
                  </Button>
                ))}
              </div>
            </div>

            <Button
              onClick={handleGenerate}
              disabled={generateContentMutation.isPending || !topic.trim() || !contentType}
              className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium hover:shadow-lg transition-all duration-200 flex items-center justify-center space-x-2 disabled:opacity-50 disabled:cursor-not-allowed border-0"
              data-testid="button-generate-content"
            >
              {generateContentMutation.isPending ? (
                <>
                  <Loader2 className="w-5 h-5 animate-spin" />
                  <span>{t('content.generate')}</span>
                </>
              ) : (
                <>
                  <Zap className="w-5 h-5" />
                  <span>{t('content.generate')}</span>
                </>
              )}
            </Button>
          </TabsContent>

          <TabsContent value="manual" className="space-y-4">
            <div>
              <Label htmlFor="manual-content" className="text-sm font-medium text-foreground mb-2 block">
                {t('content.yourContent')}
              </Label>
              <Textarea
                id="manual-content"
                placeholder={t('content.manualInput.placeholder')}
                value={manualContent}
                onChange={(e) => setManualContent(e.target.value)}
                className="w-full min-h-[200px] resize-y"
                data-testid="textarea-manual-content"
              />
              <div className="flex justify-between items-center mt-2">
                <span className="text-xs text-muted-foreground">
                  {manualContent.length} {t('content.characters')}
                </span>
                {manualContent.length >= 10 && (
                  <div className="flex items-center gap-1 text-xs text-green-600">
                    <CheckCircle className="w-3 h-3" />
                    {t('content.readyToUse')}
                  </div>
                )}
              </div>
            </div>

            <Button
              onClick={handleManualSubmit}
              disabled={!manualContent.trim() || manualContent.trim().length < 10}
              className="w-full bg-primary text-primary-foreground font-medium hover:shadow-lg transition-all duration-200 flex items-center justify-center space-x-2 disabled:opacity-70 disabled:cursor-not-allowed"
              data-testid="button-submit-manual-content"
            >
              <Edit3 className="w-5 h-5" />
              <span>{t('content.useThisContent')}</span>
            </Button>
          </TabsContent>
        </Tabs>

        <Button
          onClick={handleSaveDraft}
          disabled={saveDraftMutation.isPending || !user}
          variant="secondary"
          className="w-full mt-4"
          data-testid="button-save-draft"
        >
          {saveDraftMutation.isPending ? (
            <>
              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
              {t('button.saving')}
            </>
          ) : (
            t('button.saveAsDraft')
          )}
        </Button>
      </CardContent>
    </Card>
  );
}

client/src/components/content-preview.ts.content:
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { useWallet } from "@/hooks/use-wallet";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useState, useEffect } from "react";
import { Eye, Send, Edit, User, Loader2, MessageCircle, Repeat, Heart, Share, ExternalLink } from "lucide-react";
import { useLanguage } from "@/components/language-provider";

interface PexelsPhoto {
  id: number;
  src: {
    medium: string;
    large: string;
  };
  alt: string;
  photographer: string;
}

interface UploadedImage {
  id: string;
  src: {
    medium: string;
    large: string;
  };
  alt: string;
  photographer: string;
  isUploaded: true;
  file: File;
}

type ImageType = PexelsPhoto | UploadedImage;

interface ContentPreviewProps {
  content: string;
  selectedImage: ImageType | null;
  contentSource: 'ai' | 'manual' | null;
  onContentChange: (content: string) => void;
  onResetFields?: () => void;
}

export function ContentPreview({ content, selectedImage, contentSource, onContentChange, onResetFields }: ContentPreviewProps) {
  const [isEditing, setIsEditing] = useState(false);
  const [editedContent, setEditedContent] = useState(content);
  const [showFidModal, setShowFidModal] = useState(false);
  const [farcasterFid, setFarcasterFid] = useState("");
  const [pendingPublishData, setPendingPublishData] = useState<{ content: string; imageUrl?: string } | null>(null);
  const [castPreparation, setCastPreparation] = useState<{ farcasterUrl: string; castContent: string } | null>(null);
  const { user, displayName, updateUser } = useWallet();
  const { toast } = useToast();
  const { t } = useLanguage();

  // Native Farcaster compose function using Mini Apps SDK
  const openNativeFarcasterCompose = async (content: string, imageUrl?: string) => {
    try {
      // Import and use Farcaster Mini Apps SDK
      const { sdk } = await import('@farcaster/miniapp-sdk');

      // Prepare embeds array (SDK expects specific array types)
      const embeds: [] | [string] | [string, string] = imageUrl ? [imageUrl] : [];

      // Use native Farcaster compose with content and image
      const result = await sdk.actions.composeCast({
        text: content,
        embeds: embeds,
      });

      console.log('Farcaster compose result:', result);

      toast({
        title: t('content.castOpened'),
        description: t('content.nativeFarcasterOpened'),
      });

      // Reset fields after native cast success
      if (onResetFields) {
        setTimeout(() => {
          onResetFields();
        }, 1000); // Small delay to let user see the success message
      }

      return true;
    } catch (error) {
      console.warn('Native Farcaster compose not available, falling back to URL:', error);
      return false;
    }
  };

  const publishMutation = useMutation({
    mutationFn: async (data: { content: string; imageUrl?: string }) => {
      const preparedContent = data.content.trim();

      // Try native compose first
      const nativeSuccess = await openNativeFarcasterCompose(preparedContent, data.imageUrl);

      if (!nativeSuccess) {
        // Create Farcaster compose URL as fallback
        let farcasterUrl = `https://warpcast.com/~/compose?text=${encodeURIComponent(preparedContent)}`;

        // Add image to the compose URL if available
        if (data.imageUrl) {
          farcasterUrl += `&embeds[]=${encodeURIComponent(data.imageUrl)}`;
        }

        return {
          castContent: preparedContent,
          farcasterUrl: farcasterUrl,
          success: true,
          useNative: false
        };
      }

      return {
        castContent: preparedContent,
        success: true,
        useNative: true
      };
    },
    onSuccess: (data) => {
      if (!data.useNative && data.farcasterUrl) {
        // Open Farcaster URL in new tab as fallback
        window.open(data.farcasterUrl, '_blank');

        toast({
          title: t('content.castPrepared'),
          description: t('content.shareManually'),
        });
      }

      // Reset all fields after successful cast
      if (onResetFields) {
        setTimeout(() => {
          onResetFields();
        }, 1000); // Small delay to let user see the success message
      }
    },
    onError: (error: any) => {
      // Check if error is due to missing Farcaster FID
      if (error.message && error.message.includes("Farcaster FID not set")) {
        setShowFidModal(true);
        setPendingPublishData({
          content: content.trim(),
          imageUrl: selectedImage?.src.large,
        });
      } else {
        toast({
          title: t('content.publishingFailed'),
          description: error.message || t('content.failedToPublish'),
          variant: "destructive",
        });
      }
    },
  });

  const handleSaveEdit = () => {
    onContentChange(editedContent);
    setIsEditing(false);
    toast({
      title: t('content.contentUpdated'),
      description: t('content.changesSaved'),
    });
  };

  const handleCancelEdit = () => {
    setEditedContent(content);
    setIsEditing(false);
  };

  const handlePublish = () => {
    if (!content.trim()) {
      toast({
        title: t('content.noContentToPrepare'),
        description: t('content.generateContentFirst'),
        variant: "destructive",
      });
      return;
    }

    // Check wallet requirement only for AI content
    if (contentSource === 'ai' && !user) {
      toast({
        title: t('wallet.aiContentRequiresWallet'),
        description: t('wallet.connectWalletToCastAI'),
        variant: "destructive",
      });
      return;
    }

    publishMutation.mutate({
      content: content.trim(),
      imageUrl: selectedImage?.src.large,
    });
  };

  // Update local edited content when external content changes
  useEffect(() => {
    setEditedContent(content);
  }, [content]);

  return (
    <Card className="content-card border border-border">
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-foreground flex items-center">
            <Eye className="w-5 h-5 mr-2 text-accent" />
            {t('content.preview')}
          </h3>
          <Button
            variant="secondary"
            size="sm"
            onClick={() => setIsEditing(!isEditing)}
            data-testid="button-edit-content"
          >
            <Edit className="w-4 h-4 mr-1" />
            {isEditing ? t('content.cancel') : t('content.edit')}
          </Button>
        </div>

        {/* Cast Preview */}
        <div className="border border-border rounded-xl p-4 bg-muted/50 mb-6">
          <div className="flex items-start space-x-3 mb-3">
            <div className="w-8 h-8 bg-primary/20 rounded-full flex items-center justify-center">
              <User className="w-4 h-4 text-primary" />
            </div>
            <div className="flex-1">
              <div className="flex items-center space-x-2">
                <span className="font-medium text-foreground" data-testid="preview-username">
                  {displayName || t('preview.yourUsername')}
                </span>
                <span className="text-sm text-muted-foreground">{t('preview.now')}</span>
              </div>
            </div>
          </div>

          {/* Content Area */}
          <div className="space-y-3 mb-4">
            {isEditing ? (
              <div className="space-y-3">
                <Textarea
                  value={editedContent}
                  onChange={(e) => setEditedContent(e.target.value)}
                  className="min-h-[120px] resize-none"
                  placeholder={t('content.editPlaceholder') || 'Edit your content here...'}
                  data-testid="textarea-edit-content"
                />
                <div className="flex gap-2">
                  <Button 
                    size="sm" 
                    onClick={handleSaveEdit}
                    data-testid="button-save-edit"
                  >
                    {t('content.saveChanges')}
                  </Button>
                  <Button 
                    size="sm" 
                    variant="secondary" 
                    onClick={handleCancelEdit}
                    data-testid="button-cancel-edit"
                  >
                    {t('content.cancel')}
                  </Button>
                </div>
              </div>
            ) : (
              <>
                <div className="text-foreground leading-relaxed whitespace-pre-wrap" data-testid="preview-content">
                  {content || (
                    <span className="text-muted-foreground italic">
                      {t('content.generatedContentPlaceholder') || 'Generated content will appear here...'}
                    </span>
                  )}
                </div>
                {content && (
                  <p className="text-muted-foreground text-sm flex items-center">
                    <Eye className="w-4 h-4 mr-1" />
                    Generated by FarcastAI
                  </p>
                )}
              </>
            )}
          </div>

          {/* Selected Image */}
          {selectedImage && (
            <div className="mb-4">
              <img
                src={selectedImage.src.medium}
                alt={selectedImage.alt}
                className="w-full max-h-64 object-contain rounded-lg"
                data-testid="preview-image"
              />
            </div>
          )}

          {/* Engagement Preview */}
          <div className="flex items-center justify-between pt-3 border-t border-border">
            <div className="flex items-center space-x-6">
              <button className="flex items-center space-x-2 text-muted-foreground hover:text-foreground transition-colors">
                <MessageCircle className="w-4 h-4" />
                <span className="text-sm">Reply</span>
              </button>
              <button className="flex items-center space-x-2 text-muted-foreground hover:text-foreground transition-colors">
                <Repeat className="w-4 h-4" />
                <span className="text-sm">Recast</span>
              </button>
              <button className="flex items-center space-x-2 text-muted-foreground hover:text-foreground transition-colors">
                <Heart className="w-4 h-4" />
                <span className="text-sm">Like</span>
              </button>
            </div>
            <button className="text-muted-foreground hover:text-foreground transition-colors">
              <Share className="w-4 h-4" />
            </button>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="space-y-4">
          <Button
            onClick={handlePublish}
            disabled={publishMutation.isPending || !content.trim()}
            className="w-full farcaster-purple text-white font-medium hover:shadow-lg transition-all duration-200 flex items-center justify-center space-x-2"
            data-testid="button-publish-to-farcaster"
          >
            {publishMutation.isPending ? (
              <>
                <Loader2 className="w-5 h-5 animate-spin" />
                <span>{t('content.preparing')}</span>
              </>
            ) : (
              <>
                <Send className="w-5 h-5" />
                <span>{t('content.prepare')}</span>
              </>
            )}
          </Button>

          {/* Cast Preparation Result */}
          {castPreparation && (
            <div 
              className="bg-green-50 dark:bg-green-950 border border-green-200 dark:border-green-800 rounded-lg p-4 space-y-3"
              data-testid="cast-preparation-result"
            >
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                <p className="text-sm font-medium text-green-700 dark:text-green-300">
                  Cast hazırlandı! Manuel olarak Farcaster'da paylaşın
                </p>
              </div>

              <div className="bg-white dark:bg-gray-800 rounded-lg p-3 border">
                <p className="text-xs text-muted-foreground mb-2">Hazırlanan içerik:</p>
                <p className="text-sm text-foreground">{castPreparation.castContent}</p>
              </div>

              <Button
                onClick={() => window.open(castPreparation.farcasterUrl, '_blank')}
                className="w-full bg-purple-600 hover:bg-purple-700 text-white"
                data-testid="button-open-farcaster"
              >
                <ExternalLink className="w-4 h-4 mr-2" />
                Farcaster'da Aç ve Paylaş
              </Button>

              <Button
                variant="ghost"
                size="sm"
                onClick={() => setCastPreparation(null)}
                className="w-full text-muted-foreground"
                data-testid="button-close-preparation"
              >
                Kapat
              </Button>
            </div>
          )}

          {/* Demo Mode Indicator - Only show when demo mode is enabled */}
          {import.meta.env.VITE_DEMO_MODE === 'true' && (
            <div 
              className="bg-blue-50 dark:bg-blue-950 border border-blue-200 dark:border-blue-800 rounded-lg p-3"
              data-testid="status-demo-mode"
            >
              <p className="text-sm text-blue-700 dark:text-blue-300 text-center">
                ⚡ Demo Mode: Casts are simulated - Not sent to real Farcaster
              </p>
            </div>
          )}

        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/corner-wallet-widg.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { useWallet } from "@/hooks/use-wallet";
import { 
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { 
  Popover, 
  PopoverContent, 
  PopoverTrigger 
} from "@/components/ui/popover";
import { 
  Wallet, 
  User, 
  LogOut, 
  Check,
  ChevronDown
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useLanguage } from "@/components/language-provider";

export function CornerWalletWidget() {
  const [isOpen, setIsOpen] = useState(false);
  const [isConnectMenuOpen, setIsConnectMenuOpen] = useState(false);
  const { isConnected, address, ensName, ensAvatar, displayName, user, connecting, connect, connectFarcasterWallet, disconnect, usdcBalance, ethBalance, solBalance } = useWallet();
  const { t } = useLanguage();
  const { toast } = useToast();


  const handleFarcasterConnect = async () => {
    setIsConnectMenuOpen(false);
    try {
      const success = await connectFarcasterWallet();
      if (success) {
        toast({
          title: t('wallet.farcasterConnected'),
          description: t('wallet.farcasterConnectedDesc'),
        });
      } else {
        throw new Error("Failed to connect Farcaster wallet");
      }
    } catch (error) {
      console.error("Farcaster connection failed:", error);
      toast({
        title: t('wallet.connectionFailed'),
        description: t('wallet.connectionFailedDesc'),
        variant: "destructive",
      });
    }
  };


  const handleDisconnect = () => {
    disconnect();
    setIsOpen(false);
    toast({
      title: t('wallet.disconnected'),
      description: t('wallet.disconnectedDesc'),
    });
  };

  const getDisplayName = () => {
    // Priority: Farcaster username > Farcaster display name > ENS name > shortened address
    if (user?.farcasterUsername) return `@${user.farcasterUsername}`;
    if (user?.farcasterDisplayName) return user.farcasterDisplayName;
    if (ensName) return ensName;
    if (displayName) return displayName;
    if (address) return `${address.slice(0, 6)}...${address.slice(-4)}`;
    return t('wallet.unknown');
  };

  // Connected state - show username
  if (isConnected && address) {
    return (
      <div className="md:fixed md:top-4 md:right-24 z-50">
        <Popover open={isOpen} onOpenChange={setIsOpen}>
          <PopoverTrigger asChild>
            <Button
              variant="outline"
              className="bg-background/95 backdrop-blur-sm border border-border hover:bg-accent/10 transition-all duration-200 shadow-lg"
              data-testid="button-wallet-connected"
            >
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
                {user?.farcasterAvatar ? (
                  <img 
                    src={user.farcasterAvatar} 
                    alt="Farcaster Avatar" 
                    className="w-5 h-5 rounded-full object-cover"
                    onError={(e) => {
                      // Fall back to user icon if image fails to load
                      (e.currentTarget as HTMLImageElement).style.display = 'none';
                      (e.currentTarget.nextElementSibling as HTMLElement)!.style.display = 'block';
                    }}
                  />
                ) : ensAvatar ? (
                  <img 
                    src={ensAvatar} 
                    alt="ENS Avatar" 
                    className="w-5 h-5 rounded-full object-cover"
                    onError={(e) => {
                      // Fall back to user icon if image fails to load
                      (e.currentTarget as HTMLImageElement).style.display = 'none';
                      (e.currentTarget.nextElementSibling as HTMLElement)!.style.display = 'block';
                    }}
                  />
                ) : null}
                <User className={`w-4 h-4 ${user?.farcasterAvatar || ensAvatar ? 'hidden' : ''}`} />
                <span className="font-medium">{getDisplayName()}</span>
                <ChevronDown className="w-3 h-3 opacity-50" />
              </div>
            </Button>
          </PopoverTrigger>

          <PopoverContent 
            className="w-80 p-0 mr-4" 
            side="bottom" 
            align="end"
            data-testid="popover-wallet-details"
          >
            <div className="p-4 space-y-4">
              {/* Header */}
              <div className="flex items-center space-x-3">
                {user?.farcasterAvatar ? (
                  <img 
                    src={user.farcasterAvatar} 
                    alt="Farcaster Avatar" 
                    className="w-8 h-8 rounded-full object-cover border-2 border-green-500/20"
                    onError={(e) => {
                      // Fall back to standard check icon if image fails to load
                      (e.currentTarget as HTMLImageElement).style.display = 'none';
                      (e.currentTarget.nextElementSibling as HTMLElement)!.style.display = 'flex';
                    }}
                  />
                ) : ensAvatar ? (
                  <img 
                    src={ensAvatar} 
                    alt="ENS Avatar" 
                    className="w-8 h-8 rounded-full object-cover border-2 border-green-500/20"
                    onError={(e) => {
                      // Fall back to standard check icon if image fails to load
                      (e.currentTarget as HTMLImageElement).style.display = 'none';
                      (e.currentTarget.nextElementSibling as HTMLElement)!.style.display = 'flex';
                    }}
                  />
                ) : null}
                <div className={`w-8 h-8 bg-green-500/20 rounded-full flex items-center justify-center ${user?.farcasterAvatar || ensAvatar ? 'hidden' : ''}`}>
                  <Check className="w-4 h-4 text-green-500" />
                </div>
                <div>
                  <p className="font-semibold text-foreground">{t('wallet.connected')}</p>
                  <p className="text-sm text-muted-foreground">{t('wallet.farcasterConnectedDesc')}</p>
                </div>
              </div>

              {/* User Info */}
              <div className="space-y-2 p-3 bg-muted/50 rounded-lg">
                {ensName && (
                  <div>
                    <p className="text-xs text-muted-foreground">{t('wallet.ensName')}</p>
                    <p className="font-medium text-orange-500" data-testid="text-ens-name">{ensName}</p>
                  </div>
                )}

                {user?.farcasterDisplayName && (
                  <div>
                    <p className="text-xs text-muted-foreground">{t('wallet.displayName')}</p>
                    <p className="font-medium" data-testid="text-display-name">{user.farcasterDisplayName}</p>
                  </div>
                )}

                {user?.farcasterUsername && (
                  <div>
                    <p className="text-xs text-muted-foreground">{t('wallet.username')}</p>
                    <p className="font-medium text-primary" data-testid="text-username">@{user.farcasterUsername}</p>
                  </div>
                )}

                {user?.farcasterFid && (
                  <div>
                    <p className="text-xs text-muted-foreground">{t('wallet.farcasterID')}</p>
                    <p className="font-mono text-sm" data-testid="text-farcaster-id">{user.farcasterFid}</p>
                  </div>
                )}

                {/* Balance Display */}
                {(usdcBalance || ethBalance || solBalance) && (
                  <div>
                    <p className="text-xs text-muted-foreground">Bakiyeler</p>
                    <div className="flex items-center space-x-2 mt-1">
                      {usdcBalance && usdcBalance !== '0.00' && (
                        <span className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 px-2 py-1 rounded text-sm font-medium flex items-center space-x-1">
                          <span className="text-green-600">$</span>
                          <span>${usdcBalance} USDC</span>
                        </span>
                      )}
                      {ethBalance && ethBalance !== '0.0000' && (
                        <span className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded text-sm font-medium flex items-center space-x-1">
                          <span className="text-blue-600">Ξ</span>
                          <span>{ethBalance} ETH</span>
                        </span>
                      )}
                      {solBalance && solBalance !== '0.0000' && (
                        <span className="bg-purple-100 dark:bg-purple-900 text-purple-800 dark:text-purple-200 px-2 py-1 rounded text-sm font-medium flex items-center space-x-1">
                          <span className="text-purple-600">◎</span>
                          <span>{solBalance} SOL</span>
                        </span>
                      )}
                    </div>
                  </div>
                )}

                <div>
                  <p className="text-xs text-muted-foreground">{t('wallet.address')}</p>
                  <div className="flex items-center space-x-2">
                    <p className="font-mono text-sm" data-testid="text-wallet-address">
                      {address ? `${address.slice(0, 6)}...${address.slice(-4)}` : 'Loading...'}
                    </p>
                    {address && (
                      <button 
                        onClick={() => window.open(`https://basescan.org/address/${address}`, '_blank')}
                        className="text-blue-500 hover:text-blue-700 transition-colors"
                        data-testid="button-basescan-link"
                      >
                        <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                        </svg>
                      </button>
                    )}
                  </div>
                </div>
              </div>

              {/* Disconnect Button */}
              <Button
                variant="destructive"
                onClick={handleDisconnect}
                className="w-full"
                data-testid="button-disconnect"
              >
                <LogOut className="w-4 h-4 mr-2" />
                {t('wallet.disconnect')}
              </Button>
            </div>
          </PopoverContent>
        </Popover>
      </div>
    );
  }

  // Disconnected state - show connect options
  return (
    <div className="md:fixed md:top-4 md:right-24 z-50">
      <Dialog open={isConnectMenuOpen} onOpenChange={setIsConnectMenuOpen}>
        <DialogTrigger asChild>
          <Button
            variant="outline"
            className="bg-blue-600 hover:bg-blue-700 text-white border-blue-600 hover:border-blue-700 backdrop-blur-sm transition-all duration-200 shadow-lg font-medium"
            disabled={connecting}
            data-testid="button-wallet-connect"
          >
            <div className="flex items-center space-x-2">
              <Wallet className="w-4 h-4" />
              <span className="font-medium">
                {connecting ? t('wallet.connecting') : t('wallet.connect')}
              </span>
            </div>
          </Button>
        </DialogTrigger>

        <DialogContent className="sm:max-w-md md:max-w-lg mx-auto my-auto border-0 shadow-2xl bg-white dark:bg-gray-900 rounded-3xl overflow-hidden">
          <div className="p-6">
            <DialogHeader className="text-center space-y-3 mb-6">
              <div className="mx-auto w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-2xl flex items-center justify-center">
                <Wallet className="w-6 h-6 text-white" />
              </div>
              <DialogTitle className="text-xl font-semibold text-gray-900 dark:text-white">
                {t('wallet.connectYourWallet')}
              </DialogTitle>
              <DialogDescription className="text-sm text-gray-600 dark:text-gray-400">
                {t('wallet.chooseWalletToConnect')}
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-3">
              <Button
                variant="outline"
                onClick={handleFarcasterConnect}
                disabled={connecting}
                className="w-full h-auto p-4 border-2 hover:border-purple-200 dark:hover:border-purple-800 hover:bg-purple-50 dark:hover:bg-purple-950/30 transition-all duration-200 rounded-2xl"
                data-testid="button-connect-farcaster"
              >
                <div className="flex items-center space-x-4 w-full">
                  <div className="w-12 h-12 bg-purple-500/10 rounded-xl flex items-center justify-center">
                    <User className="w-6 h-6 text-purple-600 dark:text-purple-400" />
                  </div>
                  <div className="text-left flex-1">
                    <p className="font-semibold text-gray-900 dark:text-white">{t('wallet.farcaster')}</p>
                    <p className="text-sm text-gray-500 dark:text-gray-400">{t('wallet.connectWallet')}</p>
